mapper.select:
select u.nickname, v.uid, v.title, v.duration, v.cover_url, v.upload_date, v.vid, vs.play, vs.danmu, vs.good, vs.bad, vs.coin, vs.collect, vs.share, vs.comment from (select video.vid, video.uid, video.title, video.mc_id, video.duration, video.cover_url, video.upload_date, video.visible, video.status from video
visible = 0 and status = 1
and video.title like concat('%', #{queryVo.title}, '%')
and video.duration >= #{queryVo.startDuration}
and video.duration <= #{queryVo.endDuration}
and video.upload_date >= #{queryVo.startTime}
and video.upload_date <= #{queryVo.endTime}
and video.mc_id = #{queryVo.mcId}
and video.uid = #{queryVo.uid}
) v left join (select user.uid, user.nickname from user) u on v.uid = u.uid left join (select vid, play, danmu, good, bad, coin, collect, share, comment from video_stats) vs on v.vid = vs.vid
order by ${queryVo.sortField} desc

select u.nickname, v.uid, v.title, v.duration, v.cover_url, v.upload_date, v.vid, vs.play, vs.danmu, vs.good, vs.bad, vs.coin, vs.collect, vs.share, vs.comment from (select video.vid, video.uid, video.title, video.mc_id, video.duration, video.cover_url, video.upload_date, video.visible, video.status from video
visible = 0 and status = 1 and vid in
#{vid}
) v left join (select user.uid, user.nickname from user) u on v.uid = u.uid left join (select vid, play, danmu, good, bad, coin, collect, share, comment from video_stats) vs on v.vid = vs.vid order by play
